mapper.select:
 		SELECT rnum, free_no, comment_no, comment_writer, comment_content, comment_regist_date, comment_modify_date, comment_use_yn, upper_no, comment_depth
		FROM (
		  SELECT ROWNUM as rnum, free_no, comment_no, comment_writer, comment_content, comment_regist_date, comment_modify_date, comment_use_yn, upper_no, comment_depth
		  FROM (
		    SELECT free_no, comment_no, comment_writer, comment_content, comment_regist_date, comment_modify_date, comment_use_yn, upper_no, comment_depth
		    FROM comments
		    ORDER BY comment_no ASC
		  )
		  WHERE rownum <= #{endRowNo}  
		)
		WHERE rnum >= #{startRowNo}
